const rad2Deg = (rad) => (rad * 180.0) / Math.PI;
const deg2rad = (deg) => (deg * Math.PI) / 180.0;
const toFixed1 = (num) => Number(num).toFixed(1);
const toFixed2 = (num) => Number(num).toFixed(2);
const lerp = (x, y, t) => {
    return (1 - t) * x + t * y;
};
export { rad2Deg, deg2rad, toFixed1, toFixed2, lerp };
